mapper.select:
select Id
from hr_rebaterecord re


        BriefChapterId = #{briefchapterId}


        and RebateType = #{rebateType}

    and DeleteFlag = 0

mapper.update:
update hr_rebaterecord



        rebate_variable = #{rebateVariable}



where DeleteFlag = 0

    and BriefChapterId = #{briefchapter}


    and RebateType = #{rebateType}

update hr_rebaterecord


        RebateMale = #{manMoney},


        RebateFemale = #{womenMoney}


        DeleteFlag = 0

            and RebateType = #{type}


            and BriefChapterId = #{briefchapter}

update.foreach:
update hr_rebaterecord

    RebateMale = #{item.rebateMale} ,
    RebateFemale = #{item.rebateFemale},
    RebateTime = #{item.rebateTime}


    BriefChapterId = #{item.briefchapterId} and RebateType = #{item.rebateType}

mapper.update:
update hr_rebaterecord

    status = 1,

        RebateTime = #{rebateTime}

    where

        Id = #{id}

    and DeleteFlag = 0

mapper.select:
select re.status, re.RebateFemale, re.RebateMale, re.RebateTime, re.RebateType, re.status, re.id, re.signup_deliveryrecord_id, hsd.id as hsdId from hr_rebaterecord re left join hr_signup_deliveryrecord hsd on re.signup_deliveryrecord_id = hsd.id and hsd.delete_flag = 0 left join hr_briefchapter br on re.BriefChapterId = hsd.brief_chapter_id and br.briefChapterStatus = 0 left join hr_signup si on si.id = hsd.signup_id and si.delete_flag = 0 where re.DeleteFlag = 0
and si.id = #{signUpId}
and br.Id = #{briefchapterId}
order by re.CreateTime asc

mapper.update:
update hr_rebaterecord

    status = 1,

        signup_deliveryrecord_id = #{signupDeliveryrecordId}


        DeleteFlag = 0

            and Id = #{rebateId}
