mapper.update:
update hr_signup

    delete_flag = 1


            id = #{signUpId}

update hr_signup_deliveryrecord sd, hr_signup si


        sd.no_report_reason = #{reason}



        sd.delete_flag = 0
        and si.delete_flag = 0
        and si.id = sd.signup_id

            and si.id = #{signUpId}

update hr_signup_deliveryrecord sd, hr_signup si
sd.job_status = 0
sd.delete_flag = 0 and si.delete_flag = 0 and si.id = sd.signup_id
and sd.brief_chapter_id = #{briefChapterId}
and si.id in
#{item}

update hr_acct

    AcctBalance = (AcctBalance + #{addMoney} )

        DeleteFlag = 0

            and UserId = #{userId}


            and sign_up_id = #{signUpId}

update hr_acct

    IceBalance = (IceBalance - #{subtraction})

        DeleteFlag = 0

            and UserId = #{userId}


            and sign_up_id = #{signUpId}

mapper.select:
select s.user_name as userName, s.id as id, s.age as age, u.UserName as commendName, sd.job_status as jobStatus, s.arrival_time as arrivalTime, ifnull(b.id, '0') as noSingUp, u.type from hr_signup s left join hr_user u on s.user_id = u.Id and u.DeleteFlag = 0 left join hr_signup_deliveryrecord sd on s.id = sd.signup_id and sd.delete_flag = 0 left join hr_briefchapter b on sd.brief_chapter_id = b.Id and b.DeleteFlag = 0
s.delete_flag = 0
and s.sex = #{sex}
and s.education = #{education}
and s.profession = #{profession}
and s.age = #{age}
and arrival_time between now() and adddate(now(), interval #{arrivalTime} day)
and YEAR(graduation_time) = YEAR(DATE_SUB(NOW(), INTERVAL #{graduationTime} YEAR))
and YEAR(graduation_time) <= YEAR(DATE_SUB(NOW(), INTERVAL #{graduationTime} YEAR))
and s.relation = #{relation}
and (u.type = 1 or u.type = 2)

select s.user_name as userName, s.id as signUpId, s.user_id as userId, s.sex as sex, s.id_card as cardId, s.education as education, s.arrival_time as arrivalTime, s.graduation_time as graduationTime, s.expected_salary_lower as expectedSalaryLower, s.expected_salary_upper as expectedSalaryUpper, sd.job_status as jobStatus, ifnull(b.Id, 0) as noSignUp, s.relation as relation, sd.brief_chapter_id as briefChapterId, b.Id as briefChapterId, dii.name as postName FROM hr_signup s LEFT JOIN hr_signup_deliveryrecord sd ON s.id = sd.signup_id and sd.delete_flag = 0 LEFT JOIN hr_briefchapter b ON sd.brief_chapter_id = b.Id and b.DeleteFlag = 0 LEFT JOIN hr_dictionary_item dii ON b.PostId = dii.value and dii.status = 1 and pId = 1 where s.delete_flag = 0
and s.id = #{id}
group by s.user_name

select msut.id, s.user_name, s.sex, s.id_card, s.education, s.graduation_time, s.profession, s.arrival_time, s.expected_salary_upper, s.expected_salary_lower, s.relation, s.it_is_public, s.agree_platform_help, (SELECT GROUP_CONCAT(d.name) FROM hr_dictionary_item d WHERE FIND_IN_SET(d.value, s.registration_position_id) and d.pId = 1) as Name, s.id as signId, s.user_id from my_sign_up_table msut left join hr_signup s on msut.sign_id = s.id
s.id = #{signId}
and s.user_id = #{userId}

select s.user_name, s.user_id, msut.sign_id as signId from my_sign_up_table msut
left join hr_signup s on msut.sign_id = s.id


        s.user_id = #{userId}

select u.HeadImage, u.UserName as user_name, s.id_card, s.relation, s.registration_position_id, u.RecommendNoLower, u.RecommendNoUpper, u.RecommendInfo, s.it_is_public, s.agree_platform_help from hr_user u left join hr_signup s on u.Id = s.user_id and s.delete_flag = 0
u.DeleteFlag = 0 and u.type = 2
and u.Id = #{userId}

select si.user_name, u.UserName as userName, si.id from hr_signup si left join hr_signup_deliveryrecord sd on si.id = sd.signup_id and sd.delete_flag = 0 left join hr_user u on si.user_id = u.Id and u.DeleteFlag = 0 left join hr_briefchapter br on br.id = sd.brief_chapter_id and br.DeleteFlag = 0
si.delete_flag = 0
and br.Id = #{briefChapterId}
and u.id = #{userId}

select distinct s.user_name
from hr_signup s
left join hr_user u on s.user_id = u.Id

    s.delete_flag = 0

        and u.type = #{type}



        and u.Id = #{userId}

select sd.job_status, sd.brief_chapter_id, si.user_name, sd.signup_id, sd.brief_chapter_id from hr_signup si inner join hr_signup_deliveryrecord sd on si.id = sd.signup_id and sd.delete_flag = 0 inner join hr_user u on si.user_id = u.id and u.DeleteFlag = 0
si.delete_flag = 0
and si.user_name = #{jobUserName}

select a.girlNum, b.boyNum, c.man_num as manNum, d.women_num as womenNum
from (select count(*) as girlNum from hr_signup where delete_flag = 0 and sex = 0

    and brief_chapter_id = #{briefchapter}

) a,
(select count(*) as boyNum from hr_signup where delete_flag = 0 and sex = 1

    and brief_chapter_id = #{briefchapter}

) b,
(select man_num from hr_briefchapter where DeleteFlag = 0

    and Id = #{briefchapter}

) c,
(select women_num from hr_briefchapter where DeleteFlag = 0

    and id = #{briefchapter}

) d;

select re.status, re.RebateFemale, re.RebateMale, re.CreateTime, re.RebateType, si.id, si.user_id, re.Id as rebateId from hr_rebaterecord re left join hr_briefchapter br on re.BriefChapterId = br.id and br.DeleteFlag = 0 left join hr_signup_deliveryrecord sd on br.Id = sd.brief_chapter_id and sd.delete_flag = 0 left join hr_signup si on sd.signup_id = si.id and si.delete_flag = 0 left join hr_user u on si.user_id = u.Id and si.delete_flag = 0
re.DeleteFlag = 0
and u.Id = #{userId}

select si.user_name from my_sign_up_table st left join hr_signup si on si.id = st.sign_id and st.delete_flag = 0
si.delete_flag = 0
and st.status = #{status}

        select si.user_name as signName,
       si.id
from hr_signup si
         left join hr_signup_deliveryrecord sd on si.id = sd.signup_id and sd.delete_flag = 0
         left join hr_user u on si.user_id = u.Id and u.DeleteFlag = 0
         left join hr_briefchapter br on br.id = sd.brief_chapter_id and br.DeleteFlag = 0
where si.delete_flag = 0

  and br.id = #{brieId}


  and u.type = #{typeId}


  and u.id = #{userId}
